import { useState } from "react";

export default function Home() {




  return (
    <>
    <h1>Home</h1>
    <ProductTable />
    </>

  );
}

export function ProductTable(){
  let categoryList = [];

  PRODUCTS.forEach( product => {
    if ( !categoryList.includes(product.category)){
      categoryList.push(product.category);
    }
  })

  // const ProductCategories = categoryList.map( category =>{
  //   return <ProductCategory category={category} key={category}/>
  // });

  return (
    <table>
      <tr>
        <th>Name</th><th>Price</th>
      </tr>
      <ProductCategory category={categoryList[0]} key={categoryList[0]}/>
      {/* <ProductCategories /> */}
    </table>
  )
}

export function ProductCategory({category}){
  return (
    <tr><th colSpan='2'>{category}</th></tr>
  );
}



export const PRODUCTS = [
  {category: "Fruits", price: "$1", stocked: true, name: "Apple"},
  {category: "Fruits", price: "$1", stocked: true, name: "Dragonfruit"},
  {category: "Fruits", price: "$2", stocked: false, name: "Passionfruit"},
  {category: "Vegetables", price: "$2", stocked: true, name: "Spinach"},
  {category: "Vegetables", price: "$4", stocked: false, name: "Pumpkin"},
  {category: "Vegetables", price: "$1", stocked: true, name: "Peas"}
];
